package Day2;

public class Char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'c';
		System.out.println(ch);

		char ch1 = 80;
		System.out.println(ch1);

		char var1 = '\u00B6';
		System.out.println(var1);

		char var2 = '\u20AC';
		System.out.println(var2);

		int a = 'B';
		System.out.println(a);
	}

}
